use ambient_native_std::mesh::{Mesh, MeshBuilder};
use anyhow::bail;
use glam::{vec2, vec3};
use itertools::Itertools;
use yaml_rust::Yaml;

use crate::parse_unity_yaml;

/// A unity .asset file
pub struct Asset {
    pub mesh: Mesh,
}
impl Asset {
    pub fn from_string(data: &str) -> anyhow::Result<Self> {
        Self::from_yaml(parse_unity_yaml(data)?.pop().unwrap())
    }
    pub fn from_yaml(asset: Yaml) -> anyhow::Result<Self> {
        // From: https://docs.unity3d.com/ScriptReference/Rendering.VertexAttribute.html
        #[repr(usize)]
        #[allow(dead_code)]
        enum UnityVertexAttribs {
            Position,
            Normal,
            Tangent,
            Color,
            TexCoord0,
            TexCoord1,
            TexCoord2,
            TexCoord3,
            TexCoord4,
            TexCoord5,
            TexCoord6,
            TexCoord7,
            BlendWeight,
            BlendIndices,
        }
        let vertex_count = asset["Mesh"]["m_VertexData"]["m_VertexCount"]
            .as_i64()
            .unwrap() as usize;
        let vertex_data_size = asset["Mesh"]["m_VertexData"]["m_DataSize"]
            .as_i64()
            .unwrap() as usize;
        let vertex_size = vertex_data_size / vertex_count;
        struct Channel {
            offset: usize,
            dimension: usize,
        }
        let channels = asset["Mesh"]["m_VertexData"]["m_Channels"]
            .as_vec()
            .unwrap()
            .iter()
            .map(|channel| Channel {
                offset: channel["offset"].as_i64().unwrap() as usize,
                dimension: channel["dimension"].as_i64().unwrap() as usize,
            })
            .collect_vec();
        let vertex_data = asset["Mesh"]["m_VertexData"]["_typelessdata"]
            .as_str()
            .unwrap();
        let vertex_data = parse_unity_typeless_data(vertex_data);

        let positions = if channels[UnityVertexAttribs::Position as usize].dimension > 0 {
            (0..vertex_count)
                .map(|i| {
                    let offset = (i * vertex_size
                        + channels[UnityVertexAttribs::Position as usize].offset)
                        / 4;
                    vec3(
                        vertex_data[offset],
                        vertex_data[offset + 1],
                        vertex_data[offset + 2],
                    )
                })
                .collect_vec()
        } else {
            bail!("Unity mesh contains no vertex positions");
        };
        let normals = if channels[UnityVertexAttribs::Normal as usize].dimension > 0 {
            (0..vertex_count)
                .map(|i| {
                    let offset = (i * vertex_size
                        + channels[UnityVertexAttribs::Normal as usize].offset)
                        / 4;
                    vec3(
                        vertex_data[offset],
                        vertex_data[offset + 1],
                        vertex_data[offset + 2],
                    )
                })
                .collect_vec()
        } else {
            Vec::new()
        };
        let tangents = if channels[UnityVertexAttribs::Tangent as usize].dimension > 0 {
            (0..vertex_count)
                .map(|i| {
                    let offset = (i * vertex_size
                        + channels[UnityVertexAttribs::Tangent as usize].offset)
                        / 4;
                    vec3(
                        vertex_data[offset],
                        vertex_data[offset + 1],
                        vertex_data[offset + 2],
                    )
                })
                .collect_vec()
        } else {
            Vec::new()
        };
        let texcoords = (0..8)
            .filter_map(|texcoord| {
                if channels[UnityVertexAttribs::TexCoord0 as usize + texcoord].dimension > 0 {
                    Some(
                        (0..vertex_count)
                            .map(|i| {
                                let offset = (i * vertex_size
                                    + channels[UnityVertexAttribs::TexCoord0 as usize + texcoord]
                                        .offset)
                                    / 4;
                                vec2(vertex_data[offset], 1. - vertex_data[offset + 1])
                            })
                            .collect_vec(),
                    )
                } else {
                    None
                }
            })
            .collect_vec();

        let index_data = asset["Mesh"]["m_IndexBuffer"].as_str().unwrap();
        let indices = parse_unity_index_data(index_data);

        let mesh_builder = MeshBuilder {
            positions,
            normals,
            tangents,
            texcoords,
            indices,
            ..MeshBuilder::default()
        };
        let mesh = mesh_builder.build()?;

        Ok(Asset { mesh })
    }
}

fn parse_unity_typeless_data(input: &str) -> Vec<f32> {
    // From: https://gist.github.com/maierfelix/1802f9523dee4090e16dc44a4ac70176
    fn hex2float(num: i32) -> f32 {
        let sign = if num < 0 { -1. } else { 1. };
        let exponent = ((num >> 23) & 0xff) - 127;
        let mantissa = 1. + ((num & 0x7fffff) as f32 / 0x7fffff as f32);
        sign * mantissa * (2f32).powi(exponent)
    }
    fn swap32(val: i64) -> i32 {
        (((val << 24) & 0xff000000)
            | ((val << 8) & 0xff0000)
            | ((val >> 8) & 0xff00)
            | ((val >> 24) & 0xff)) as i32
    }
    (0..(input.len() / 8))
        .map(|i| {
            let res = i64::from_str_radix(&input[i * 8..(i + 1) * 8], 16).unwrap();
            hex2float(swap32(res))
        })
        .collect_vec()
}
#[test]
fn test_parse_unity_typeless_data() {
    let input = "";

    let res = parse_unity_typeless_data(input);
    let start_expected = [
        -1.676, 2.5738, -0.0933, 0., 0., 1., -1., 0., 0., -1., 0.4506, 0.4698, 0.4506, 0.4698,
        -1.9162, 1.38, -0.0408, 0., 0., 1., -1., -0., 0., -1., 0.4614, 0.2544, 0.4614, 0.2544,
    ];
    for i in 0..start_expected.len() {
        assert!((start_expected[i] - res[i]).abs() < 0.01);
    }
}
fn parse_unity_index_data(input: &str) -> Vec<u32> {
    fn swap16(val: i16) -> i16 {
        ((val & 0xFF) << 8) | ((val >> 8) & 0xFF)
    }
    (0..(input.len() / 4))
        .map(|i| {
            let p = i16::from_str_radix(&input[i * 4..(i + 1) * 4], 16).unwrap();
            swap16(p) as u32
        })
        .collect()
}
#[test]
fn test_parse_unity_index_data() {
    let input = "000001000200000003000400030000000200030005000400050006000400070003000200080009000a000b000c000a0008000a000c000b000d000c000b000e000d0008000c000f00100011001200100013001400150010001200150013001000130016001400170015001200180019001a001b001c001a0018001a001d001a001c001d001b001e001c0018001d001f00";
    let res = parse_unity_index_data(input);
    let expected = vec![
        0, 1, 2, 0, 3, 4, 3, 0, 2, 3, 5, 4, 5, 6, 4, 7, 3, 2, 8, 9, 10, 11, 12, 10, 8, 10, 12, 11,
        13, 12, 11, 14, 13, 8, 12, 15, 16, 17, 18, 16, 19, 20, 21, 16, 18, 21, 19, 16, 19, 22, 20,
        23, 21, 18, 24, 25, 26, 27, 28, 26, 24, 26, 29, 26, 28, 29, 27, 30, 28, 24, 29, 31,
    ];
    assert_eq!(res, expected);
}
